package com.apnacollege;
import java.util.*;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int x=sc.nextInt();
		
		if(x % 2==0) {
			System.out.println(x+" is even");
		}
		else
			System.out.println(x+" is odd");

	}

}
